        const directionBtn = productDirection.querySelector('.container__products-direction__btn')
        const productTotal = productDirection.querySelector('.container__products-direction-total')
        productTotal.innerText = `${productDirectionInput.value}/${itemWrapList}`

        directionBtn.onclick = function() {
            directionBtn.setAttribute('href',`#item-wrap${productDirectionInput.value}`)
            productTotal.innerText = `${productDirectionInput.value}/${itemWrapList}`
        }
        const prevPage = productDirection.querySelector('.container__products-direction.prev-page');
        const nextPage = productDirection.querySelector('.container__products-direction.next-page');
        prevPage.onclick = function() {
            var pageCurent = productDirectionInput.value;
            if (pageCurent>1) {
                productDirectionInput.value =  pageCurent-=1;
                prevPage.setAttribute('href', `#item-wrap${pageCurent}`);
                productTotal.innerText = `${pageCurent}/${itemWrapList}`;
            }
        }
        nextPage.onclick = function() {
            if(productDirectionInput.value < itemWrapList) {
                productDirectionInput.value = Number(productDirectionInput.value)+1;
                nextPage.setAttribute('href', `#item-wrap${productDirectionInput.value}`);
                productTotal.innerText = `${productDirectionInput.value}/${itemWrapList}`;
            };
        }
